import "./add-cell.css";
import { useActions } from "../hooks/use-actions";
import { Button, Icon, Divider } from "semantic-ui-react";

interface AddCellProps {
	nextCellId: string | null;
	forceVisible?: boolean;
}

const AddCell = ({ nextCellId, forceVisible }: AddCellProps) => {
	const { insertCellBefore } = useActions();

	return (
		<div className={forceVisible ? "force-visible" : ""}>
			<Divider horizontal>
				<Button.Group className="add-cell" compact>
					<Button
						animated="fade"
						className="add-cell-button"
						onClick={() => insertCellBefore(nextCellId, "code")}
					>
						<Button.Content visible>Code</Button.Content>
						<Button.Content hidden>
							<Icon name="add" />
						</Button.Content>
					</Button>
					<Button
						animated="fade"
						className="add-cell-button"
						onClick={() => insertCellBefore(nextCellId, "text")}
					>
						<Button.Content visible>Text</Button.Content>
						<Button.Content hidden>
							<Icon name="add" />
						</Button.Content>
					</Button>
				</Button.Group>
			</Divider>
		</div>
	);
};

export default AddCell;
