import * as esbuild from "esbuild-wasm";

export const unpkgPathPlugin = () => {
	return {
		name: "unpkg-path-plugin",
		setup(build: esbuild.PluginBuild) {
			// esbuild figuring out where the index.js file is stored
			build.onResolve({ filter: /.*/ }, async (args: any) => {
				console.log("onResolve", args);
				// returns the found path
				if (argss.path === "index.js") {
				}
				return { path: args.path, namespace: "a" };
				// namespace is similar to how filter is used
				// if namespace: 'a' is set on onResolve, and namespace: 'b' is set on onLoad for a certain file, it will not be loaded
			});
			// esbuild loading the file
			build.onLoad({ filter: /.*/ }, async (args: any) => {
				console.log("onLoad", args);
				// instead of allowing esbuild to normally load the contents of the file,
				if (args.path === "index.js") {
					// hardcoded content returned for index.js
					return {
						loader: "jsx",
						contents:
							`
                            import message from 'tiny-test-pkg';
                            console.log(message);
                        `
					};
				}
			});
		}
	};
};
