import * as esbuild from "esbuild-wasm";
import axios from "axios";
import localforage from "localforage";

const fileCache = localforage.createInstance({
	name: "filecache"
});

export const fetchPlugin = (inputCode: string) => {
	return {
		// name is mainly to identify this plugin for debugging purposes
		name: "fetch-plugin",
		setup(build: esbuild.PluginBuild) {
			// onLoad event listener (override esbuild's default file loading process)
			build.onLoad({ filter: /.*/ }, async (args: any) => {
				if (args.path === "index.js") {
					return {
						loader: "jsx",
						contents: inputCode
					};
				}

				// // Check to see if this file has already been fetched and if it is in the cache
				// const cachedResult = await fileCache.getItem<esbuild.OnLoadResult>(args.path);
				// // If it is, return it immediately
				// if (cachedResult) {
				// 	return cachedResult;
				// } else {
				// axios get request to path
				const { data, request } = await axios.get(args.path);
				const loader = args.path.match(/.css$/) ? "css" : "jsx";

				const result: esbuild.OnLoadResult = {
					loader: "jsx",
					contents: data,
					resolveDir: new URL("./", request.responseURL).pathname
				};
				// store response in cache
				await fileCache.setItem(args.path, result);
				return result;
				// }
			});
		}
	};
};
