import "./code-editor.css";
import { useRef } from "react";
import Editor, { OnMount } from "@monaco-editor/react";
import prettier from "prettier";
import parser from "prettier/parser-babel";
import { Fab, Zoom } from "@mui/material";

interface EditorProps {
	initialValue: string;
	onChange(value: string): void;
}

const CodeEditor = ({ initialValue, onChange }: EditorProps) => {
	const editorRef = useRef<any>(null);

	const onEditorMount: OnMount = (editor, monaco) => {
		editorRef.current = editor;
		onChange(editor.getValue());
	};

	const onFormatClick = () => {
		// get current value from editor instance
		const unformatted = editorRef.current.getValue();
		// format that value
		const formatted = prettier.format(unformatted, {
			parser: "babel",
			plugins: [ parser ],
			useTabs: true,
			semi: true
		});
		// set formatted value back into the editor
		editorRef.current.setValue(formatted);
	};

	return (
		<div className="editor-wrapper">
			<Zoom
				in={true}
				timeout={600}
				style={{
					transitionDelay: "1500ms"
				}}
				unmountOnExit
			>
				<Fab className="button-format" variant="extended" size="medium" color="red" onClick={onFormatClick}>
					Format
				</Fab>
			</Zoom>

			<Editor
				onMount={onEditorMount}
				value={initialValue}
				theme="vs-dark"
				language="javascript"
				height="50vh"
				options={{
					wordWrap: "on",
					minimap:
						{
							enabled: false
						},
					showUnused: false,
					folding: false,
					lineNumbersMinChars: 3,
					fontSize: 16,
					scrollBeyondLastLine: false,
					automaticLayout: true
				}}
			/>
		</div>
	);
};

export default CodeEditor;
