import Editor from "@monaco-editor/react";

const CodeEditor = () => {
	return (
		<Editor
			theme="vs-dark"
			language="javascript"
			height="50vh"
			options={{
				wordWrap: "on",
				minimap:
					{
						enabled: false
					},
				showUnused: false,
				folding: false,
				lineNumbersMinChars: 3,
				fontSize: 16,
				scrollBeyondLastLine: false
			}}
		/>
	);
};

export default CodeEditor;
