import "./code-cell.css";
import { useEffect } from "react";
import CodeEditor from "./code-editor";
import Preview from "./preview";
import Resizable from "./resizable";
import { Cell } from "../state";
import { useActions } from "../hooks/use-actions";
import { useTypedSelector } from "../hooks/use-typed-selector";
import { Loader, Segment } from "semantic-ui-react";

interface CodeCellProps {
	cell: Cell;
}

const CodeCell = ({ cell }: CodeCellProps) => {
	const { updateCell, createBundle } = useActions();
	const bundle = useTypedSelector((state) => state.bundles[cell.id]);
	const cumulativeCode = useTypedSelector((state) => {
		// destructure data and order array from state
		const { data, order } = state.cells;
		// map over each cell id from order array and return corresponding cell from data array
		const orderedCells = order.map((id) => data[id]);
		const cumulativeCode = [];
		for (let c of orderedCells) {
			if (c.type === "code") {
				cumulativeCode.push(c.content);
			}
			if (c.id === cell.id) {
				break;
			}
		}
	});

	useEffect(
		() => {
			const timer = setTimeout(async () => {
				createBundle(cell.id, cell.content);
			}, 750);

			return () => {
				clearTimeout(timer);
			};
		},
		[ cell.id, cell.content, createBundle ]
	);

	return (
		<Resizable direction="vertical">
			<div style={{ height: "calc(100% - 10px)", display: "flex", flexDirection: "row" }}>
				<Resizable direction="horizontal">
					<CodeEditor initialValue={cell.content} onChange={(value) => updateCell(cell.id, value)} />
				</Resizable>
				{!bundle || bundle.loading ? (
					<Segment>
						<Loader active indeterminate>
							Loading...
						</Loader>
					</Segment>
				) : (
					<Preview code={bundle.code} bundleError={bundle.err} />
				)}
			</div>
			{/* <Segment>
				<Loader active indeterminate>
					Loading...
				</Loader>
			</Segment> */}
		</Resizable>
	);
};

export default CodeCell;
