import "./add-cell.css";
import { useActions } from "../hooks/use-actions";
import { Button, Icon } from "semantic-ui-react";

interface AddCellProps {
	nextCellId: string | null;
}

const AddCell = ({ nextCellId }: AddCellProps) => {
	const { insertCellBefore } = useActions();

	return (
		<Button.Group>
			<Button animated="fade" className="add-cell-button" onClick={() => insertCellBefore(nextCellId, "code")}>
				<Button.Content visible>Next</Button.Content>
				<Button.Content hidden>
					<Icon name="arrow right" />
				</Button.Content>
				<Icon name="add" />
				Code
			</Button>
			<Button animated="fade" className="add-cell-button" onClick={() => insertCellBefore(nextCellId, "text")}>
				<Icon name="add" />
				Text
			</Button>
		</Button.Group>
	);
};

export default AddCell;
