import { useActions } from "../hooks/use-actions";
import { Button } from "semantic-ui-react";

interface ActionBarProps {
	id: string;
}

const ActionBar = ({ id }: ActionBarProps) => {
	const { moveCell, deleteCell } = useActions();

	return (
		<div className="action-bar">
			<Button className="action-bar-button action-bar-up" onClick={() => moveCell(id, "up")}>
				Up
			</Button>
			<Button className="action-bar-button action-bar-down" onClick={() => moveCell(id, "down")}>
				Down
			</Button>
			<Button className="action-bar-button action-bar-delete" onClick={() => deleteCell(id)}>
				Delete
			</Button>
		</div>
	);
};

export default ActionBar;
